/**
 * @file
 * CSS styles for <Axis />
 */

import { css } from '@emotion/core';

export const baseAxisStyles = css`
  font-size: inherit !important;
`;

export const webAxisStyles = css`
  ${baseAxisStyles}

  text {
    font-family: metric, MetricWeb;
    fill: #66605c;
    font-size: 16px;
  }

  line {
    stroke: #e6d9ce;
    stroke-width: 1px;
  }

  .visx-axis-banding rect {
    fill: #ccc1b7;
    opacity: 0.4;
  }

  /* tick text on yAxis */
  .visx-axis-left text,
  .visx-axis-right text {
    fill: #66605c;
  }

  .visx-axis-left text {
    text-anchor: end;
  }

  .visx-axis-right text {
    text-anchor: start;
  }

  /* tick line on yAxis */
  .visx-axis-left line,
  .visx-axis-right line {
    stroke: #e6d9ce;
    stroke-width: 1px;
  }

  /* tick text on xAxis */
  .visx-axis-top text,
  .visx-axis-bottom text {
    fill: #66605c;
    text-anchor: middle;
  }

  /* tick line on xAxis */
  .visx-axis-top line,
  .visx-axis-bottom line {
    stroke: #e6d9ce;
    stroke-width: 1px;
  }
`;

