/**
 * @file
 * Recreation of g-chartframe using visx
 */

import React from 'react';
import Frame from './base';
import FramePrint from './print';
import FrameSocial from './social';
import FrameVideo from './video';
import { chartFramePropTypes } from '../prop-types';
import { chartFrameDefaultProps } from '../default-props';

/* eslint-disable react/jsx-props-no-spreading */
const ChartFrame = ({ type, ...props }) => {
  const typeLower = type.toLowerCase();

  switch (typeLower) {
    case 'print':
      return <FramePrint type={type} {...props} />;
    case 'social':
      return <FrameSocial type={typeLower} {...props} />;
    case 'video':
      return <FrameVideo type={type} {...props} />;
    case 'web':
    default:
      return <Frame type={typeLower} {...props} />;
  }
};
/* eslint-enable react/jsx-props-no-spreading */

ChartFrame.propTypes = chartFramePropTypes;
ChartFrame.defaultProps = chartFrameDefaultProps;

export default ChartFrame;
export { PlotContext } from './plot-context';
