import React from 'react';
import Frame from './base';
import { convertMMtoPX } from '../../util';
import { printChartFrameStyles } from './styles';
import { framePrintPropTypes } from '../prop-types';
import { framePrintDefaultProps } from '../default-props';

const FramePrint = ({ type, ...props }) => {
  // convert mm measures for width and height into pixels
  const { width: mmWidth, height: mmHeight } = props;
  const pxWidth = convertMMtoPX(mmWidth);
  const pxHeight = convertMMtoPX(mmHeight);

  return (
    <Frame
      css={printChartFrameStyles}
      {...props} // eslint-disable-line react/jsx-props-no-spreading
      type={type}
      width={pxWidth}
      height={pxHeight}
    />
  );
};

FramePrint.propTypes = framePrintPropTypes;
FramePrint.defaultProps = framePrintDefaultProps;

export default FramePrint;
