import React from 'react';
import Frame from './base';
import { socialChartFrameStyles } from './styles';
import { frameSocialPropTypes } from '../prop-types';
import { frameSocialDefaultProps } from '../default-props';

const FrameSocial = ({ type, ...props }) => (
  <Frame
    css={socialChartFrameStyles}
    {...props} // eslint-disable-line react/jsx-props-no-spreading
    type={type}
  />
);

FrameSocial.propTypes = frameSocialPropTypes;
FrameSocial.defaultProps = frameSocialDefaultProps;

export default FrameSocial;
