import React from 'react';
import Frame from './base';
import { videoChartFrameStyles } from './styles';
import { frameVideoPropTypes } from '../prop-types';
import { frameVideoDefaultProps } from '../default-props';

const FrameVideo = ({ type, ...props }) => (
  <Frame
    css={videoChartFrameStyles}
    {...props} // eslint-disable-line react/jsx-props-no-spreading
    type={type}
  />
);

FrameVideo.propTypes = frameVideoPropTypes;
FrameVideo.defaultProps = frameVideoDefaultProps;

export default FrameVideo;
