/**
 * @file
 * A wrapper used for demonstrating event handling
 */

import React, { useState } from 'react';

const EventWrapper = ({ children }) => {
  const [[value, event], setValue] = useState(['', {}]);
  console.log(event);
  console.log(value);
  return (
    <div>
      <h3>
        <pre>Active element: {JSON.stringify(value)}</pre>
        <pre>Event: {JSON.stringify(event)}</pre>
      </h3>
      {children(setValue)}
    </div>
  );
};

export default EventWrapper;
