function initDebug(instance) {
	let debug = false;

	return function(value) {
		if (value === undefined) return debug;
		debug = !!value;
		return instance;
	};
}


function initDebugColor(instance) {
	let color = "red";

	return function(value) {
		if (value === undefined) return color;
		color = value;
		return instance;
	};
}


export { initDebug, initDebugColor };
