function initZeroAxis(instance) {
	var zero_axis = false;
	return function(value) {
		if (value === undefined) return zero_axis;
		zero_axis = !!value;
		return instance;
	};
}


function initFlipAxis(instance) {
	let flip = false;

	return function(value) {
		if (value === undefined) return flip;
		flip = !!value;
		return instance;
	};
}


export { initFlipAxis, initZeroAxis };
