import { initXAxisUpdating } from "./x-axis";
import { initYAxisUpdating } from "./y-axis";
import { initY2AxisUpdating } from "./y2-axis";


function initAxisUpdating(instance, state) {
	var updateXAxes = initXAxisUpdating(instance, state);
	var updateYAxes = initYAxisUpdating(instance, state);
	var updateY2Axes = initY2AxisUpdating(instance, state);

	return function() {
		var x = updateXAxes();
		var y = updateYAxes();
		var y2 = updateY2Axes();
		return {
			x: x,
			y: y,
			y2: y2
		};
	};
}


export { initAxisUpdating };
