import { ascending } from "d3-array";

function isAscending(arr) {
	var prev = -Infinity;
	return arr.every(function(d) {
		if (d >= prev) {
			prev = d;
			return true;
		}
	});
}

function isDescending(arr) {
	var prev = Infinity;
	return arr.every(function(d) {
		if (d <= prev) {
			prev = d;
			return true;
		}
	});
}

export { ascending, isAscending, isDescending };
