function isAscending(arr) {
	if (arr.length < 2) return true;
	var prev = arr[0];
	return arr.every(function(d) {
		if (d >= prev) {
			prev = d;
			return true;
		}
	});
}

function isDescending(arr) {
	if (arr.length < 2) return true;
	var prev = arr[0];
	return arr.every(function(d) {
		if (d <= prev) {
			prev = d;
			return true;
		}
	});
}

export { isAscending, isDescending };
