const { ascending, descending } = require("d3-array");

function sortAscending(arr) { return arr.slice().sort(ascending); }
function sortDescending(arr) { return arr.slice().sort(descending); }
function reverse(arr) { return arr.slice().reverse(); }
function arrayMin(arr) { return Math.min.apply(null, arr); }
function arrayMax(arr) { return Math.max.apply(null, arr); }
function arraySum(arr) { return arr.reduce((s, d) => s += d, 0); }

module.exports = {
	sortAscending,
	sortDescending,
	reverse,
	arrayMin,
	arrayMax,
	arraySum
};
