function remToPx(rem) {
	return rem * parseFloat(getComputedStyle(document.documentElement).fontSize);
}

function log10(val) {
	return Math.log(val) / Math.LN10;
}

function roundDown(val) {
	// Round x down to nice value (preserve the first significant digit, the remaining digits -> 0)
	// e.g. 123.456 => 100, 0.123456 -> 0.1

	var exp = Math.floor(log10(val));
	var normalised_val = val / Math.pow(10, exp);
	var normalised_val_floored = Math.floor(normalised_val);
	var rounded = normalised_val_floored * Math.pow(10, exp);
	// Remove floating point garbage from decimal values
	if (exp < 0) rounded = parseFloat(rounded.toFixed(-exp));

	return rounded;
}

export { remToPx, roundDown };
